import React, { useState, useEffect } from "react";
import { Box, Typography, TextField, InputBase, Grid } from "@mui/material";
import theme from "../../../theme/theme";
import Avatar from "../../atoms/Avatar";
import ImageUploadOptions from "../../molecules/ImageUploadOptions/index";
import User from "../../../../public/assets/images/user.svg";
import UploadImage from "../../../assets/icons/upload.png";
import LockIcon from "../../../assets/icons/lock-icon.png";
import ThreeDots from "../../../assets/icons/more.png";
import BoxIcon from "../../../assets/icons/Boxplus-icon.png";
import ImageUploadProgress from "../../molecules/ImageUploadProgress";

const container = {
  width: "676px",
  height: "379px",
  backgroundColor: theme.palette.structural[50],
  boxShadow: "0px 2px 6px rgba(0, 0, 0, 0.2)",
  marginTop: "10px",
};
const imageBox = {
  alignItems: "center",
  justifyContent: "center",
  paddingTop: "27px",
  paddingLeft: "28px",
  paddingRight: "28px",

  backgroundColor: theme.palette.accent?.[200],
};

const avatarBox = {
  borderRadius: "0%",
  paddingLeft: "10px",
  height: "157px",
  width: "159px",
  boxSizing: "border-box",
};
const textColor = {
  color: theme.palette.structural[50],
};
const nameBox = {
  alignItems: "center",
  justifyContent: "center",
  display: "flex",
  flexDirection: "column",
  paddingTop: "37px",
  paddingLeft: "25px",
  paddingRight: "40px",
};
const borderDashed = {
  border: `2px dashed #7E64F3`,
};
const portfolioText = {
  fontWeight: 700,
  fontSize: "50px",
  alignItems: "center",
  display: "flex",
  lineHeight: "56px",

  color: "text.primary",
  width: "310px",
  height: "125px",
};
const secondText = {
  fontWeight: 400,
  fontSize: "12px",
  paddingLeft: "2px",
  marginTop: "10px",
  lineHeight: "18px",

  color: "text.secondary",
  width: "283px",
  height: "60px",
  alignItems: "center",
  display: "flex",
};
const inputBox = {
  alignItems: "center",
  justifyContent: "space-between",
  paddingRight: "80px",
  paddingTop: "38px",
  marginLeft: "73px",
};
const fileUpload = () => {
  document.getElementById("upload-button")?.click();
};
const options = [
  {
    src: UploadImage,
    action: fileUpload,
  },
  {
    src: LockIcon,
  },
  {
    src: ThreeDots,
  },
  {
    src: BoxIcon,
  },
];

export type FirstPageInResumeProps = {
  userName: string;
  designation: string;
  heading: string;
  description: string;
};
const FirstPageInResume: React.FC<FirstPageInResumeProps> = (props) => {
  const [fileName, setFileName] = useState("");
  const [userImage, setUserImage] = useState<any>(User);
  const [progress, setProgress] = React.useState(0);
  const [showUploadOption, setShowUploadOption] = useState(false);
  const [showSnackBar, setShowSnackBar] = useState(false);
  const [textEdit, setTextEdit] = useState(false);
  const showHoverBoxOnEdit = () => {
    if (textEdit) {
      setTextEdit(false);
    } else {
      setTextEdit(true);
    }
  };

  const showOptions = (e: any) => {
    if (showUploadOption) {
      setShowUploadOption(false);
    } else {
      setShowUploadOption(true);
    }
  };
  const submitFile = async (e: any) => {
    const formData = new FormData();

    formData.append("multipartFile", e.target.files[0]);
    setFileName(e.target.files[0].name);
    setShowSnackBar(true);
    setUserImage(URL.createObjectURL(e.target.files[0]));
  };
  React.useEffect(() => {
    const timer = setInterval(() => {
      setProgress((oldProgress) => {
        if (oldProgress === 100) {
          setShowSnackBar(false);
          return 0;
        }
        const diff = Math.random() * 10;
        return Math.min(oldProgress + diff, 100);
      });
    }, 500);

    return () => {
      clearInterval(timer);
    };
  }, []);
  const cancelUpload = () => {
    setUserImage(User);
    setFileName("User.png");
    setShowSnackBar(false);
  };
  return (
    <>
      <Grid container sx={container}>
        <Grid item xs={4} sx={imageBox}>
          <Box sx={{ position: "absolute", top: "0px" }}>
            {showUploadOption ? (
              <ImageUploadOptions
                options={options}
                data-testid="upload-options"
              />
            ) : (
              <></>
            )}
          </Box>
          {showUploadOption ? (
            <Box sx={[avatarBox, borderDashed]}>
              <Avatar
                variant="circular"
                data-testid="avatar"
                src={userImage}
                onClick={(e) => showOptions(e)}
              />
            </Box>
          ) : (
            <Box sx={avatarBox}>
              <Avatar
                variant="circular"
                data-testid="avatar"
                src={userImage}
                onClick={(e) => showOptions(e)}
              />
            </Box>
          )}

          <Box>
            <input
              accept="image/*"
              type="file"
              id="upload-button"
              data-testid="upload-button"
              style={{ display: "none" }}
              onChange={submitFile}
            />
          </Box>

          <Box sx={nameBox}>
            <Typography variant="caption2" sx={textColor}>
              {props.userName}
            </Typography>
            <Typography variant="caption2" sx={textColor}>
              {props.designation}
            </Typography>
          </Box>
        </Grid>
        <Grid item xs={8}>
          <Box sx={inputBox}>
            {textEdit ? (
              <InputBase
                multiline
                sx={[portfolioText, borderDashed]}
                onClick={showHoverBoxOnEdit}
                value={props.heading}
                data-testid="hover-input1"
              />
            ) : (
              <InputBase
                multiline
                sx={portfolioText}
                onClick={showHoverBoxOnEdit}
                value={props.heading}
                data-testid="input1"
              />
            )}
            <InputBase
              multiline
              data-testid="input2"
              sx={secondText}
              value={props.description}
            />
          </Box>
          {showSnackBar ? (
            <ImageUploadProgress
              data-testid="progress-bar"
              imageName={fileName}
              progress={progress}
              onClose={cancelUpload}
            />
          ) : (
            <></>
          )}
        </Grid>
      </Grid>
    </>
  );
};

export default FirstPageInResume;
