import { Button as MuiButton, Grid } from '@mui/material'
import { makeStyles } from '@material-ui/core'
import Link from '@mui/material/Link';

import Icon from '../../Atoms/Icon/Icon'
import TypographyComponent from '../../Atoms/Typography/TypographyComponent'
import { useState } from 'react';

export type sidePanelProps = {
  iconUrl: string
  text: string
  classname?:string
}

export const SidePanelItem = (props: sidePanelProps) => {
  const { iconUrl, text,classname } = props

  const useStyles = makeStyles((theme) => ({
    icon: {
      paddingLeft: 'auto',
    },
    text:{
        fontWeight:400,
        fontSize:'12px',
        lineHeight:'18px'
    },
    Item: {
      width:'200px',
      backgroundColor:flag?'#EEEBFF':'#FFFFFF'
    },
  }))


  var[flag,setFlag]=useState(false)

  function handleOnClick()
  {
    setFlag(true)

  }
  const classes = useStyles()

  return (
    <Link  underline="none" onClick={()=>handleOnClick()} >

    <Grid
      container
      direction="column"
      spacing={1}
      style={{
        textAlign: 'center',
        width: '100px',
      }}
      className={classes.Item}

    >
      <Grid item>
        <Icon imageSrc={iconUrl} classname={classes.icon}></Icon>
      </Grid>
      <Grid item style={{  textAlign: 'center' }}>
        <TypographyComponent
          variant="body1"
          text={text}
          color="#5A5766"
          classname={classes.text}
        ></TypographyComponent>
      </Grid>
    </Grid>
    </Link>
  )
}
